// Install Bootstrap by running: npm install bootstrap

import React, { useState, createContext, useContext } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

// Create a context for the shopping cart
const ShoppingCartContext = createContext();

const ProductListingPage = () => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', price: 25 },
    { id: 2, name: 'Product 2', price: 30 },
    { id: 3, name: 'Product 3', price: 15 },
    { id: 4, name: 'Product 4', price: 15 },
    { id: 5, name: 'Product 5', price: 15 },
    { id: 6, name: 'Product 6', price: 15 },
  ]);

  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="container mt-5">
      <h2>Product Listing</h2>
      <div className="mt-3">
        <input
          type="text"
          placeholder="Search products..."
          className="form-control mb-3"
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      <ProductList products={filteredProducts} />
    </div>
  );
};

const ProductList = ({ products }) => {
  return (
    <div className="row mt-3">
      {products.map((product) => (
        <div key={product.id} className="col-md-4 mb-4">
          <ProductCard product={product} />
        </div>
      ))}
    </div>
  );
};

const ProductCard = ({ product }) => {
  const { addToCart } = useContext(ShoppingCartContext);

  const handleAddToCart = () => {
    addToCart(product);
  };

  return (
    <div className="card">
      <img
        src="https://via.placeholder.com/150"
        className="card-img-top"
        alt={product.name}
      />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">${product.price}</p>
        <button className="btn btn-primary" onClick={handleAddToCart}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

const CartPage = () => {
  const { cartItems } = useContext(ShoppingCartContext);

  return (
    <div className="container mt-5">
      <h2>Your Shopping Cart</h2>
      <table className="table mt-3">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>${item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const ShoppingCartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    setCartItems((prevItems) => [...prevItems, product]);
  };

  return (
    <ShoppingCartContext.Provider value={{ cartItems, addToCart }}>
      {children}
    </ShoppingCartContext.Provider>
  );
};

const App = () => {
  return (
    <Router>
      <ShoppingCartProvider>
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link className="navbar-brand" to="/">
            Home
          </Link>
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/cart">
                Cart
              </Link>
            </li>
          </ul>
        </nav>
        <Route path="/cart" component={CartPage} />
        <Route exact path="/" component={ProductListingPage} />
      </ShoppingCartProvider>
    </Router>
  );
};

export default App;
