// ProductDescription.js
import React from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const ProductDescription = ({ products }) => {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  if (!product) {
    return <div>Product not found</div>;
  }

  // Assume product.images is an array of image URLs
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <Slider {...settings}>
            {product.images.map((image, index) => (
              <div key={index}>
                <img src={image} className="img-fluid" alt={`${product.name} - ${index + 1}`} />
              </div>
            ))}
          </Slider>
        </div>
        <div className="col-md-6">
          <h2>{product.name}</h2>
          <p>Price: ${product.price}</p>
          {/* Additional product details go here */}
          <div className="mt-3">
            <button className="btn btn-primary mr-2">Add to Cart</button>
            <button className="btn btn-outline-secondary">Buy Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDescription;
