// Install Bootstrap by running: npm install bootstrap

import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductListingPage = () => {
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', price: 25 },
    { id: 2, name: 'Product 2', price: 30 },
    { id: 3, name: 'Product 3', price: 15 },
  ]);

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showLoginModal, setShowLoginModal] = useState(false);

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleAddToCart = (product) => {
    // Check if the user is logged in
    const isLoggedIn = /* Check your authentication state here */ false;

    if (isLoggedIn) {
      // Add the product to the cart or perform the desired action
      // For now, let's just log a message
      console.log(`Added ${product.name} to the cart`);
    } else {
      // Show the login modal
      setShowLoginModal(true);
      // Store the selected product to add to the cart after login
      setSelectedProduct(product);
    }
  };

  const handleLogin = () => {
    // Implement your login logic here
    // Once the user is logged in, you can add the selected product to the cart
    console.log('User logged in');
    if (selectedProduct) {
      console.log(`Added ${selectedProduct.name} to the cart`);
      // Reset selected product and close login modal
      setSelectedProduct(null);
      setShowLoginModal(false);
    }
  };

  const handleCloseLoginModal = () => {
    // Reset selected product and close login modal
    setSelectedProduct(null);
    setShowLoginModal(false);
  };

  return (
    <div className="container mt-5">
      <h2>Product Listing</h2>
      <div className="mt-3">
        <input
          type="text"
          placeholder="Search products..."
          className="form-control mb-3"
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      <div className="row mt-3">
        {products.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card">
              <img
                src="https://via.placeholder.com/150"
                className="card-img-top"
                alt={product.name}
              />
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">${product.price}</p>
                <button
                  className="btn btn-primary"
                  onClick={() => handleAddToCart(product)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Login Modal */}
      <div
        className={`modal fade ${showLoginModal ? 'show' : ''}`}
        tabIndex="-1"
        role="dialog"
        style={{ display: showLoginModal ? 'block' : 'none' }}
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Login Required</h5>
              <button
                type="button"
                className="close"
                onClick={handleCloseLoginModal}
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <p>Please log in to add items to your cart.</p>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleLogin}
              >
                Log In
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handleCloseLoginModal}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductListingPage;
