// ProductDescription.js
import React from 'react';
import { useParams } from 'react-router-dom';

const ProductDescription = ({ products }) => {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  if (!product) {
    return <div>Product not found</div>;
  }

  // Function to render stars based on the rating
  const renderStars = (rating) => {
    const fullStars = Array.from({ length: Math.floor(rating) }, (_, index) => (
      <span key={index} className="star">&#9733;</span>
    ));
    const halfStar = rating % 1 !== 0 ? <span className="star">&#9733;</span> : null;

    return (
      <div className="product-rating">
        {fullStars}
        {halfStar}
      </div>
    );
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <img
            src="https://via.placeholder.com/300" // Replace with actual product image URL
            className="img-fluid"
            alt={product.name}
          />
        </div>
        <div className="col-md-6">
          <h2>{product.name}</h2>
          <p>Price: ${product.price}</p>
          {/* Display product rating */}
          {renderStars(product.rating)}
          {/* Additional product details go here */}
          <div className="mt-3">
            <button className="btn btn-primary mr-2">Add to Cart</button>
            <button className="btn btn-outline-secondary">Buy Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDescription;
