// ProductDescription.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const ProductDescription = ({ products, onImageUpload }) => {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  const [imageFile, setImageFile] = useState(null);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setImageFile(file);
  };

  const handleImageUpload = () => {
    // Call a function to handle the image upload, e.g., pass the file to the parent component
    if (imageFile) {
      onImageUpload(productId, imageFile);
    }
  };

  if (!product) {
    return <div>Product not found</div>;
  }

  // Check if product.images is defined
  if (!product.images || !product.images.length) {
    return <div>No images available for this product</div>;
  }

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <Slider {...settings}>
            {product.images.map((image, index) => (
              <div key={index}>
                <img src={image} className="img-fluid" alt={`${product.name} - ${index + 1}`} />
              </div>
            ))}
          </Slider>
          <div className="mt-3">
            <input type="file" onChange={handleImageChange} accept="image/*" />
            <button className="btn btn-primary mt-2" onClick={handleImageUpload}>
              Upload Image
            </button>
          </div>
        </div>
        <div className="col-md-6">
          <h2>{product.name}</h2>
          <p>Price: ${product.price}</p>
          {/* Additional product details go here */}
          <div className="mt-3">
            <button className="btn btn-primary mr-2">Add to Cart</button>
            <button className="btn btn-outline-secondary">Buy Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDescription;
