// Install Bootstrap and React Router by running: npm install bootstrap react-router-dom
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Jumbo from './Jumbo'
import Cart from './pages/Cart';

const ProductListingPage = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const [products, ] = useState([
    { id: 1, name: 'Laptop', category: 'Electronics', price: 800 },
    { id: 2, name: 'Smartphone', category: 'Electronics', price: 400 },
    { id: 3, name: 'TV', category: 'Electronics', price: 600 },
    { id: 4, name: 'Rice', category: 'Groceries', price: 20 },
    { id: 5, name: 'Milk', category: 'Groceries', price: 3 },
    { id: 6, name: 'Bread', category: 'Groceries', price: 2 },
    { id: 7, name: 'Running Shoes', category: 'Apparel', price: 50 },
    { id: 8, name: 'T-shirt', category: 'Apparel', price: 15 },
    { id: 9, name: 'Backpack', category: 'Accessories', price: 30 },
  ]);



  return (
    <Router>

        <div>
        <Routes>
          <Route path="/Jumbo" element={<Jumbo/>} />
          <Route path="/" element={<Categories />} />
          <Route path="/Cart" element={<Cart />} />
          <Route path="/electronics" element={<CategoryPage products={products} category="Electronics" searchTerm={searchTerm} />} />
          <Route path="/groceries" element={<CategoryPage products={products} category="Groceries" searchTerm={searchTerm} />} />
          <Route path="/apparel" element={<CategoryPage products={products} category="Apparel" searchTerm={searchTerm} />} />
          <Route path="/accessories" element={<CategoryPage products={products} category="Accessories" searchTerm={searchTerm} />} />
        </Routes>
      </div>
    </Router>
  );
};

const customContainerStyle = {
  maxWidth: '800px',
  marginRight: 'auto',
  marginLeft: 'auto',
};



const handleSearchChange = (event) => {
  setSearchTerm(event.target.value);
};


const Categories = () => {
  return (
    <div>
    <div style={customContainerStyle} className="container mt-5">
        <h2>Product Listing</h2>
        {/* Search Bar */}
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={handleSearchChange}
          className="form-control mb-3"
        />
        </div>
      <h3>Categories</h3>
      <ul>
        <li>
          <Link to="/electronics">Electronics</Link>
        </li>
        <li>
          <Link to="/groceries">Groceries</Link>
        </li>
        <li>
          <Link to="/apparel">Apparel</Link>
        </li>
        <li>
          <Link to="/accessories">Accessories</Link>
        </li>
        <li>
          <Link to="/Jumbo">Jumbo</Link>
        </li>
        <li>
          <Link to="/Cart">Cart</Link>
        </li>
      </ul>
    </div>
  );
};

const CategoryPage = ({ products, category, searchTerm }) => {
  const filteredProducts = products.filter(
    (product) =>
      product.category === category &&
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h3>{category} Products</h3>
      <div className="row mt-3">
        {filteredProducts.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card">
              <img
                src="https://via.placeholder.com/150"
                className="card-img-top"
                alt={product.name}
              />
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">${product.price}</p>
                <p className="card-text">Category: {product.category}</p>
                <button className="btn btn-primary">Add to Cart</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductListingPage;
