// ProductDescription.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductDescription = ({ products }) => {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleImageChange = (index) => {
    setCurrentImageIndex(index);
  };

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <div className="carousel">
            {product.images.map((image, index) => (
              <img
                key={index}
                src={image} // Use actual product image URLs from product.images array
                className={`img-fluid ${index === currentImageIndex ? 'active' : ''}`}
                alt={product.name}
                onClick={() => handleImageChange(index)}
              />
            ))}
          </div>
        </div>
        <div className="col-md-6">
          <h2>{product.name}</h2>
          <p>Price: ${product.price}</p>
          {/* Additional product details go here */}
          <div className="mt-3">
            <button className="btn btn-primary mr-2">Add to Cart</button>
            <button className="btn btn-outline-secondary">Buy Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDescription;
