// Install Bootstrap and React Router by running: npm install bootstrap react-router-dom

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductListingPage = () => {
  const [products, setProducts] = useState([
    // ... (your product data)
  ]);

  return (
    <Router>
      <div className="container mt-5">
        <h2>Product Listing</h2>
        <Routes>
          <Route path="/" element={<Categories />} />
          <Route path="/electronics" element={<CategoryPage products={products} category="Electronics" />} />
          <Route path="/groceries" element={<CategoryPage products={products} category="Groceries" />} />
          <Route path="/apparel" element={<CategoryPage products={products} category="Apparel" />} />
          <Route path="/accessories" element={<CategoryPage products={products} category="Accessories" />} />
        </Routes>
      </div>
    </Router>
  );
};

const Categories = () => {
  return (
    <div>
      <h3>Categories</h3>
      <ul>
        <li>
          <Link to="/electronics">Electronics</Link>
        </li>
        <li>
          <Link to="/groceries">Groceries</Link>
        </li>
        <li>
          <Link to="/apparel">Apparel</Link>
        </li>
        <li>
          <Link to="/accessories">Accessories</Link>
        </li>
      </ul>
    </div>
  );
};

const CategoryPage = ({ products, category }) => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredProducts = products.filter(
    (product) =>
      product.category === category &&
      product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div>
      <h3>{category} Products</h3>
      <div className="mt-3">
        <input
          type="text"
          placeholder="Search products..."
          className="form-control mb-3"
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      <div className="row mt-3">
        {filteredProducts.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            {/* ... (same card structure as before) */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductListingPage;
